<template>
  <div id="app">
        <!-- keep-alive -->
            <router-view ></router-view>
        <!-- /keep-alive -->
        <obg-button type="round" class='main-button' @click='main' >main</obg-button>
      <obg-button type="round" class='main-button sub' @click='theme' >theme</obg-button>
  </div>
</template>

<script>
import button from './components/button'
export default {
  name: 'app',
  components: {
    'obg-button': button
  },
  methods: {
    main () {
      this.$router.push('/')
    },
    theme () {
      if (this.$theme.get() === 'basic') {
        this.$theme.set('yellow')
      } else {
        this.$theme.set('basic')
      }
    }
  }
}
</script>

<style lang="scss">
  @import './styles/reset.scss';
  @import './styles/icon.scss';
  @import './styles/theme.scss';
  #app {
    height:480px;
    width:800px;
  }
  .main-button{
    position: absolute !important;
    top: 0px;
    right:0px;
    z-index:1000;
    &.sub{
      right: 0px;
      top: 80px;
    }
  }
</style>
