<template>
  <button
    class="obg-button"
    :class="['obg-button-' + type, 'obg-button-' + size, {
      'is-disabled': disabled,
      'is-icon': icon || $slots.icon,
      'icon-only': iconOnly
    }]"
    @click="handleClick"
    :disabled="disabled">
    <span class="obg-button-icon" v-if="icon || $slots.icon">
      <slot name="icon">
        <i v-if="icon" :class="'obg-icon-' + icon"></i>
      </slot>
    </span>
    <label class="obg-button-text"><slot></slot></label>
  </button>
</template>

<script>
/**
 * @class button
 * @classdesc components/button
 * @param {string} [type=default | flat | round ]
 * @param {boolean} [disabled=false]
 * @param {string} [icon]
 * @param {slot} [slot] label
 * @param {slot} [icon] icon
 *
 * @example
 * import 'obigo-js-ui/components/button'
 * <obg-button icon="back" type="default">button</obg-button>
 * <obg-button type="flat">
 *    <i class="obg-icon-back" slot="icon" />
 *    button
 * </obg-button>
 */
export default {
  name: 'obg-button',
  methods: {
    handleClick (evt) {
      this.$emit('click', evt)
    }
  },
  computed: {
    iconOnly () {
      if ((this.icon || this.$slots.icon) && typeof this.$slots.default === 'undefined') {
        return true
      }
      return false
    }
  },
  props: {
    icon: String,
    disabled: Boolean,
    type: {
      type: String,
      default: 'default',
      validator (value) {
        return ['default', 'flat', 'round'].indexOf(value) > -1
      }
    },
    size: {
      type: String,
      default: 'normal',
      validator (value) {
        return [
          'small',
          'normal',
          'large'
        ].indexOf(value) > -1
      }
    }
  }
}
</script>

<style lang="scss">
  @import '../../styles/colors.variables.scss';
  .obg-button{
    position: relative;
    display: flex;
    width: 260px;
    height: 61px;
    box-shadow: inset 0 0 0 1px $grey-7;
    color: color(white);
    background-color: color(tertiary);
    font-size: 20px;
    outline: none;
    user-select: none;
    align-items: center;
    justify-content: center;
    .obg-button-icon{
      display:flex;
      justify-content: center;
    }
    &.icon-only{
      & > span {
        margin:auto;
      }
    }
    &.is-icon{
      min-height:61px;
      & > .obg-button-text {
        max-width: 160px;
      }
    }
    &:active{
      border-color: color(secondary);
      color: color(white);
      background-color: color(secondary);
    }
    &:disabled{
      opacity: 0.5;
      pointer-events: none;
    }
    &.obg-focus{
      color:color(white);
      box-shadow: inset 0 0 0 3px color(white);
    }
    &.obg-button-flat, &.obg-button-round{
      display:block;
      background-color: color(dark);
      box-shadow:none;
      &:active{
        background-color: color(secondary);
      }
      &.obg-focus{
        color:color(white);
        box-shadow: inset 0 0 0 3px color(white);
      }
      &.is-icon > .obg-button-text {
        width: 70%;
        margin-left: 10px;
      }
      & > .obg-button-text{
        line-height:24px;
      }
    }
    &.obg-button-flat{
      width: 101px;
      height: 81px;
      &.is-icon > .obg-button-text {
        width: 80%;
      }
    }
    &.obg-button-round{
      width: 69px;
      height: 69px;
      border-radius: 69px;
    }
    &.obg-button-small{
    }
    &.obg-button-normal{
    }
    &.obg-button-large{
    }
    & > .obg-button-text {
      display:block;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      word-wrap: normal;
      max-width: 200px;
      line-height:49px;
      flex:1;
    }
  }

</style>
