<template>
  <button ref="origin" class="combo-box-button" :disabled="disabled">
    <label>{{selectedItemLabel}}</label>
    <div class="arrow" />
    <obg-popover
      ref="popover"
      anchor="bottom left"
      self="top left"
      class="combo-box"
      :style="{ height: contextMenuHeight + 'px' }"
      @open="onOpen"
      @close="onClose"
    >
      <div
        class="menu-item"
        v-for="(item, index) in options"
        :name="item.name"
        @click="onItemClick"
        v-obg-focus="{zone: focusZone, order: index + 1}"
        :style="{height: itemHeight + 'px'}"
        :class="[{
            'selected': (selectedItemIndex === index)
        }]"
      >
        <span
          class="item-content"
          :style="{lineHeight: itemHeight + 'px'}"
        >
          {{item.label}}
        </span>
      </div>
    </obg-popover>
  </button>
</template>

<script>
  /**
   * @class combo-box
   * @classdesc components/combo-box
   * @param {boolean} [disabled]
   * @param {slot} [slot]
   *
   * @example
   * import combo-box from 'obigo-js-ui/components/combo-box'
   * <obg-combo-box
   *  :options="[{name: 'opt1', label: 'Option'}, {name: 'opt2', label: 'Advanced Setting'}, {name: 'opt3', label: 'Reset'}, {name: 'opt4', label: 'Help'}, {name: 'opt5', label: 'Smart Tutoriels'}]"
   * >
   * </obg-combo-box>
   */
  import popover from '../popover'
  export default {
    name: 'obg-combo-box',
    props: {
      disable: {
        type: Boolean,
        default: false
      },
      itemHeight: {
        type: Number,
        default: 61
      },
      focusZone: {
        type: Number,
        default: 1
      },
      value: {
        type: Number,
        default: -1
      },
      options: {
        type: Array,
        required: true,
        validator (arr) {
          if (arr.length < 0 || arr.length > 5) {
            return false
          }
          arr.forEach((item) => {
            if (!(item.hasOwnProperty('name') && item.hasOwnProperty('label'))) {
              throw new Error('options should be [{ name: xxxx, label: yyyy}, ...]')
            }
          })
          return true
        }
      }
    },
    data () {
      return {
        selectedItemIndex: 0
      }
    },
    components: {
      'obg-popover': popover
    },
    computed: {
      contextMenuHeight () {
        return this.options.length * this.itemHeight + this.options.length - 3
      },
      selectedItemLabel () {
        return this.options[this.selectedItemIndex].label
      }
    },
    methods: {
      close () {
        this.$refs.popover.close()
        this.$emit('close')
      },
      open (event) {
        if (!this.disable) {
          this.$refs.popover.open(event)
          this.$emit('open')
        }
      },
      onOpen () {
        this.$emit('open')
      },
      onClose () {
        this.$emit('close')
      },
      onItemClick (e) {
        const name = e.currentTarget.getAttribute('name')
        this.selectedItemIndex = this.options.findIndex((item) => { return item.name === name })
        this.close()
      }
    },
    watch: {
      selectedItemIndex (val) {
        this.$emit('input', val)
      },
      value (val) {
        this.selectedItemIndex = val
      }
    },
    mounted () {
      this.target = this.$refs.popover.$el.parentNode
      this.target.addEventListener('contextmenu', this.open)
      if (this.value >= 0) {
        this.selectedItemIndex = this.value
      } else {
        this.selectedItemIndex = 0
      }
    },
    beforeDestroy () {
      this.target.removeEventListener('contexmenu', this.handler)
    }
  }
</script>
<style lang="scss" scoped>
  @import '../../styles/colors.variables.scss';
  button.combo-box-button {
    display: flex;
    width: 260px;
    height: 61px;
    box-shadow: inset 0 0 0 1px $grey-7;
    color: #fff;
    background-color: color(tertiary);
    font-size: 20px;
    outline: none;
    user-select: none;
    align-items: center;
    justify-content: center;
    & > label {
      width: 179px;
      padding: 0 10px 0 10px;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      word-wrap: normal;
    }
    & > div.arrow {
      height: 61px;
      width: 61px;
      border: 1px solid grey;
      &:before{
        position:absolute;
        content:"";
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 15px 10px 0 10px;
        border-color: color(white) transparent transparent transparent;
        margin-left: -11px;
        margin-top: 22px;
      }
    }
    &:active{
      background-color: color(secondary);
    }
    &:disabled{
      opacity: 0.3;
      pointer-events: none;
    }
  }
  div.combo-box{
    width: 260px;
    height: 343px;
    border: 1px solid $light-blue-9;
    overflow-y: hidden !important;
    > div.menu-item {
      height:61px;
      color:color(white);
      font-size:20px;
      text-indent:20px;
      display: flex;
      align-items: center;
      background: color(black);
      padding: 0 10px 0 10px;
      border-bottom: 1px solid $grey-7;
      & > span {
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
        word-wrap: normal;
      }
      &:last-child{
        border-bottom:none;
      }
      &:active{
        background: color(secondary);
      }
      &.obg-focus{
        box-shadow: inset 0 0 0px 3px color(white);
      }
      &.selected{
        color:color(primary);
      }
    }
  }

</style>
