<template>
  <div class='obg-dialog' v-if='dialogShow' >
    <h2 class='title' >{{title}}</h2>
    <div class='content' >
      <slot></slot>
    </div>
    <div class='footer' >
      <div class='footer-btn' >
        <obg-button @click='close' class='close-btn' type='round' >
          <i class="obg-icon-close" slot="icon" ></i>
        </obg-button>
      </div>
    </div>
  </div>
</template>

<script>
/**
 * @class dialog
 * @classdesc components/dialog
 * @param {string} [title] dialog title text
 * @param {boolean} [show] show/hide status
 * @param {slot} [content] html contents
 *
 * @example
 * import 'obigo-js-ui/components/dialog'
 * <obg-dialog title='dialog title' >
 *  <div >dialog content</div>
 * </obg-dialog>
 */
import button from '../button'
export default {
  name: 'obg-dialog',
  components: {
    'obg-button': button
  },
  methods: {
    close () {
      this.$emit('close')
    }
  },
  computed: {
    dialogShow: {
      get: function () {
        return this.show
      },
      set: function (newVal) {
        this.show = newVal
      }
    }
  },
  props: {
    title: {
      type: String,
      default: ''
    },
    show: {
      type: Boolean,
      default: false
    }
  }
}
</script>

<style lang="scss" scoped>
.obg-dialog{
  background-color:rgba(0, 0, 0, 0.74);
  position:absolute;
  display:flex;
  z-index:100;
  top:0;
  bottom:0;
  right:0;
  left:0;
  flex-direction:column;
  justify-content:center;
  align-items:stretch;
  .title{
  /*
    text-align:center;
    font-size:32px;
    line-height:200%;
    width:100%;
    height:60px;
    */
    margin:0 auto; width:90%; font-size:38px; text-align:center; overflow:hidden; text-overflow:ellipsis; white-space:nowrap; word-wrap:normal;
    height:57px; line-height:165%;
  }
  .content{
    flex:1;
    padding:10px 10px;
  }
  .footer{
    height:70px;
    display:flex;
    justify-content:center;
    .footer-btn{
      .close-btn{
      /*
        border-radius:40px;
        width:140px;
        border:1px solid #898989;
        box-shadow:none;
        */
      }
      
    }
  }
}


</style>
