<template>
    <div class='obg-grid-list-item' :class='{disable: disable}'  @click="handleClick" >
        <slot></slot>
    </div>
</template>

<script>
/**
 * obg-grid-list-item
 * @class grid-list-item
 * @classdesc components/grid-list
 *
 * @example
 *   <obg-grid-list-item >hello</obg-grid-list-item>
 *
 */

export default {
  name: 'obg-grid-list-item',
  props: {
    disable: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    handleClick (e) {
      this.$emit('click', e)
      var comps = this.$el.querySelectorAll('input, select')
      for (var i = 0; i < comps.length; i++) {
        var ev = document.createEvent('MouseEvents')
        var target = comps[i]
        ev.initMouseEvent('click', false, true, e.view, 1, target.screenX, target.screenY, target.clientX, target.clientY, e.ctrlKey, e.altKey, e.shiftKey, e.metaKey, 0, null)
        ev._constructed = true
        target.dispatchEvent(ev)
      }
    }
  }
}
</script>
<style lang="scss" scoped >
@import '../../styles/colors.variables.scss';
.obg-grid-list-item{
    border:none;
    background-color:transparent;
    position:absolute;
    color: color(white);
    padding:10px 10px;
    font-size:32px;
    &.disable{
      color:#555555 !important;
      pointer-events:none;
    }
    &.focus{
      box-shadow: inset 0 0 0 3px color(white);
    }
    &:active{
        background-color:color(secondary);

    }

}
</style>

