<template>
  <div
    class="obg-slider"
    :class="{ 'obg-slider-disabled': disabled }"
    :style="{ 'width': sliderWidth + 'px'}"
  >
    <slot name="start"></slot>
    <div class="obg-slider-content" ref="content" @mousedown.prevent="onClickBar">
      <div class="obg-slider-runway" :style="{ 'border-top-width': barHeight + 'px' }" @click="onClickBar"></div>
      <div class="obg-slider-progress" :style="{ width: progress + '%', height: barHeight + 'px' }" ></div>
      <div class="obg-slider-thumb animate-scale" ref="thumb" :style="{ left: progress + '%' }">
        <span v-if="thumbLabel" class="obg-slider-current-value">{{progress}}</span>
      </div>
    </div>
    <slot name="end"></slot>
  </div>
</template>

<script type="text/babel">
  /**
   * @class slider
   * @classdesc components/slider
   * @param {slot} [slot='start']
   * @param {slot} [slot='end']
   * @param {number} [min=0]
   * @param {number} [max=100]
   * @param {number} [step=1]
   * @param {number} [value=0]
   * @param {number} [barHeight=5]
   * @param {number} [sliderWidth=500]
   * @param {boolean} [draggable=true]
   * @param {boolean} [disabled=false]
   * @event {number} [change]
   * @event {number} [input]
   *
   * @example
   * import Slider from 'obigo-js-ui/components/slider'
   * <obg-slider
   *  class="slider"
   *  v-model="model"
   *  :min="0"
   *  :max="100"
   *  @input="onInput"
   *  @change="onChange"
   *  ></obg-slider>
   */
  import draggable from '../../features/draggable'
  export default {
    name: 'obg-slider',
    props: {
      min: {
        type: Number,
        default: 0
      },
      max: {
        type: Number,
        default: 100
      },
      step: {
        type: Number,
        default: 1
      },
      disabled: {
        type: Boolean,
        default: false
      },
      value: {
        type: Number
      },
      barHeight: {
        type: Number,
        default: 5
      },
      draggable: {
        type: Boolean,
        default: true
      },
      focus: {
        type: Boolean,
        defalut: false
      },
      sliderWidth: {
        type: Number,
        default: 525
      },
      thumbLabel: {
        type: Boolean,
        default: false
      }
    },
    computed: {
      progress () {
        const value = this.value
        if (typeof value === 'undefined' || value === null || value === 0) return 0
        if (this.value >= this.max) {
          return 100
        } else if (this.value <= this.min) {
          return 0
        }
        return Math.floor((value - this.min) / (this.max - this.min) * 100)
      }
    },
    watch: {
      focus (isFocus) {
        if (isFocus) {
          this.$refs.thumb.classList.add('focus')
        } else {
          this.$refs.thumb.classList.remove('focus')
        }
      }
    },
    mounted () {
      const thumb = this.$refs.thumb
      const content = this.$refs.content
      this.stepCount = Math.ceil((this.max - this.min) / this.step)
      const getThumbPosition = () => {
        const contentBox = content.getBoundingClientRect()
        const thumbBox = thumb.getBoundingClientRect()
        return {
          left: thumbBox.left - contentBox.left,
          top: thumbBox.top - contentBox.top
        }
      }
      if (this.draggable) {
        let dragState = {}
        draggable(thumb, {
          start: () => {
            if (this.disabled) return
            const position = getThumbPosition()
            dragState = {
              thumbStartLeft: position.left,
              thumbStartTop: position.top
            }
          },
          drag: (event) => {
            if (this.disabled) return
            const contentBox = content.getBoundingClientRect()
            const deltaX = event.pageX - contentBox.left - dragState.thumbStartLeft
            const newPosition = (dragState.thumbStartLeft + deltaX) - (dragState.thumbStartLeft + deltaX) % (contentBox.width / this.stepCount) // TODO step 단위로 할지 actual로 할지 옵션 추가 및 계산식 추가
            let newProgress = newPosition / contentBox.width
            if (newProgress < 0) {
              newProgress = 0
            } else if (newProgress > 1) {
              newProgress = 1
            }
            this.$emit('input', Math.round(this.min + newProgress * (this.max - this.min)))
          },
          end: () => {
            if (this.disabled) return
            this.$emit('input', this.value)
            dragState = {}
          }
        })
      }
    },
    methods: {
      onClickBar (event) {
        if (this.draggable) {
          const contentBox = this.$refs.content.getBoundingClientRect()
          const deltaX = event.pageX - contentBox.left
          let newProgress = Math.floor(this.max * deltaX / contentBox.width)
          if (newProgress < this.min) {
            newProgress = this.min
          } else if (newProgress > this.max) {
            newProgress = this.max
          }
          this.$emit('input', newProgress)
        }
      }
    }
  }
</script>

<style lang="scss" scoped>
  @import '../../styles/colors.variables.scss';
  .obg-slider {
    position: relative;
    display: flex;
    height: 40px;
    line-height: 40px;
    color:black;
    text-align:center;
    & > * {
      display: flex;
      display: -webkit-box;
    }
    & *[slot=start] {
      margin-right: 12px;
    }
    & *[slot=end] {
      margin-left: 12px;
    }
    & > .obg-slider-content{
      position: relative;
      flex: 1;
      margin-right: 30px;
      & > .obg-slider-runway{
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        left: 0;
        right: -15px;
        border-top-color: color(dark);
        border-top-style: solid;
      }
      & > .obg-slider-thumb{
        background-color: color(white);
        position: absolute;
        left: 0;
        top: 7px;
        width: 25px;
        height: 25px;
        border-radius: 100%;
        cursor: move;
        box-shadow: 0 1px 3px rgba(0,0,0,.4);
        display: flex;
        justify-content: center;
        align-items: center;
        &.obg-focus {
          width: 19px;
          height: 19px;
          top: 11px;
          color: color(white);
          font-weight: bold;
          background-color: color(primary);
          border: 3px solid color(white);
          box-shadow: 0px 0px 15px color(primary), 0px 0px 15px color(primary), 0px 0px 15px color(primary);
        }
        &:active{
          box-shadow: 0px 0px 50px color(primary), 0px 0px 30px color(primary), 0px 0px 20px color(primary);
          width: 40px;
          height: 40px;
          top: 0px;
          animation: thumb-scale .2s;
          & > span{
            font-size:20px;
          }
        }
        & > span {
          font-size:14px;
        }
      }
      & > .obg-slider-progress {
        position: absolute;
        display: block;
        background-color: color(primary);
        top: 50%;
        transform: translateY(-50%);
        width: 0;
      }
    }
    &.obg-slider-disabled {
      opacity: 0.5;
      user-select: none;
      pointer-events: none;
    }
  }

  @keyframes thumb-scale {
    0% {
      transform: scale(0.7)
    }
    100% {
      transform: scale(1)
    }
  }
</style>
