import Focus from '../features/focus'

export default {
  bind (el, {value}, vnode) {
    Focus.addComponent(el, value, vnode)
  },
  update (el, value) {
    // console.log(value)
  },
  unbind (el, {value}) {
    Focus.removeComponent(value)
  }
}
