class AppInterCommunication {
  constructor () {
    this.messageList = []
    if (window.applicationFramework) {
      this.application = window.applicationFramework.applicationManager.getOwnerApplication(window.document)
    }
  }
  setMessage (messageId, cb) {
    this.messageList.push({id: messageId, cb: cb})
  }
}

export default new AppInterCommunication()
