import Vue from 'vue'
import Router from 'vue-router'
import App from './App'
import routes from './routes'
import Focus from './directives/obg-focus'
import ObigoUI from './index.js'

Vue.use(ObigoUI)
Vue.use(Router)
Vue.directive('obg-focus', Focus)

// $device
const router = new Router({
  routes: routes
})
var _app = new Vue({ // eslint-disable-line
  el: '#app',
  router,
  render: h => h(App)
})
router.push('/')
