<template>
  <div class="contents">
    <section>
      <label>button group - Simple</label>
      <div class="column">
        <div class="row"></div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>2 items</div>
        <div>
          <obg-button-group ref="first">
            <obg-button-group-item v-obg-focus="{zone: 1, order: 1}" >
              First
            </obg-button-group-item>
            <obg-button-group-item v-obg-focus="{zone: 1, order: 2}" >
              Second
            </obg-button-group-item>
          </obg-button-group>
        </div>
      </div>
    </section>
    <section>
      <label>button group - init by value</label>
      <div class="column">
        <div class="row"></div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>3 items - :value=1</div>
        <div>
          <obg-button-group  :value=1 ref="second">
            <obg-button-group-item v-obg-focus="{zone: 1, order: 3}">
              First
            </obg-button-group-item>
            <obg-button-group-item v-obg-focus="{zone: 1, order: 4}">
              Second
            </obg-button-group-item>
            <obg-button-group-item v-obg-focus="{zone: 1, order: 5}">
              Third
            </obg-button-group-item>
          </obg-button-group>
        </div>
      </div>
    </section>

    <section>
      <label>button group - item properties</label>
      <label>icon(icon='play'), selected(:selected=true), disabled(:selected=true)</label>
      <div class="column">
        <div class="row"></div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>4 items</div>
        <div>
          <obg-button-group ref="third">
            <obg-button-group-item  icon='play' v-obg-focus="{zone: 1, order: 6}">
              Icon
            </obg-button-group-item>
            <obg-button-group-item  v-obg-focus="{zone: 1, order: 7}" >
              Text xxxxxxxxxxxxxxx
            </obg-button-group-item>
            <obg-button-group-item  iconPosition='left'  v-obg-focus="{zone: 1, order: 8}" selected>
              Selected
            </obg-button-group-item>
            <obg-button-group-item  iconPosition='top'  v-obg-focus="{zone: 1, order: 9}" :disabled=true>
              Disabled
            </obg-button-group-item>
          </obg-button-group>
        </div>
      </div>
    </section>

    <section>
      <label>button group - event / model</label>
      <div class="column">
        <div class="row"></div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>4 items</div>
        <div>
          <obg-button-group v-model="value" @input="onInput" ref="fourth">
            <obg-button-group-item icon='play' @click="itemClick"></obg-button-group-item>
            <obg-button-group-item icon='pause' @click="itemClick"></obg-button-group-item>
            <obg-button-group-item icon='shuffle' @click="itemClick"></obg-button-group-item>
          </obg-button-group>
        </div>
      </div>
    </section>
    <section>
      <label>value</label>
      <div class="column">
        <div class="row">event</div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>@click</div>
        <div>
          {{clickedItemIndex}}
        </div>
      </div>
      <div class="column">
        <div>@input</div>
        <div>
          {{onInputValue}}
        </div>
      </div>
      <div class="column">
        <div>v-model='value'</div>
        <div>
          {{value}}
        </div>
      </div>
      <div class="column">
        <div>update v-model</div>
        <div>
          <obg-button @click="randomValue">Random</obg-button>
        </div>
      </div>
    </section>
  </div>
</template>

<script>
  import {buttonGroup, buttonGroupItem} from '../components/button-group'
  import button from '../components/button'

  export default {
    name: 'template',
    components: {
      'obg-button-group': buttonGroup,
      'obg-button-group-item': buttonGroupItem,
      'obg-button': button
    },
    methods: {
      itemClick (val) {
        this.clickedItemIndex = val
      },
      onInput (val) {
        this.onInputValue = val
      },
      randomValue () {
        let random = Math.floor(Math.random() * 3)
        while (this.value === random) {
          random = Math.floor(Math.random() * 3)
        }
        this.value = random
      }
    },
    data () {
      return {
        value: 1,
        clickedItemIndex: -1,
        onInputValue: -1
      }
    }
  }
</script>
<style lang='scss' scoped>
  @import './css/section.scss';
</style>
