<template>
  <div class="contents">
    <obg-header title="prop title">
      <obg-temperature slot="s1" />
      <span slot="s2" >slot title</span>
      <obg-clock slot="s3" />
    </obg-header>
    <obg-spinner class='spinner' color='red' :height=200 >Loading</obg-spinner>
    <p>hello</p>
    <obg-button class='sample' @click=handleClick >Label</obg-button>
    <obg-button class='sample' disabled>Disabled</obg-button>
    <p>Icon</p>
    <obg-button class='icon' @click=handleClick icon='back'> Back </obg-button>
    <obg-button class='icon' @click=handleClick icon='play'> Play </obg-button>
    <obg-button class='icon' @click=handleClick >
      <album-icon slot='icon' />
      Custom Icon
    </obg-button>
    <obg-button class='icon' @click=handleClick >
      <album-icon slot='icon' />
    </obg-button>
    <obg-button class='icon' @click=handleClick icon='play'> </obg-button>
    <p> Plain </p>
    <obg-button class='type' type='plain'>Plain</obg-button>
    <obg-button class='type' type='plain' disabled>Disabled</obg-button>
    <p>Progress Bar</p>
    <obg-progress class='p-b-test' :value=pValue :buffer=buffer :barHeight=15 >
      <span slot='start'>start</span>
      <span slot='end'>end</span>
    </obg-progress>
    <obg-button @click=progressUp >Value Up</obg-button> <obg-button @click=progressDown >Value Down</obg-button> <obg-button @click=bufferUp >Buffer Up</obg-button> <obg-button @click=bufferDown >Buffer Down</obg-button>

    <obg-footer mask='true' @back='onBack' @more='onMore'>
      <obg-button-group v-model="btnGroup" :animated=true >
        <obg-button-group-item  icon='play'>
          Player
        </obg-button-group-item>
        <obg-button-group-item iconPosition='left' icon='music-list-selected' >
          Current
        </obg-button-group-item>
        <obg-button-group-item  iconPosition='left' selected>
          <album-icon slot='icon' />
          Player2
        </obg-button-group-item>
        <obg-button-group-item  iconPosition='top' selected>
          <album-icon slot='icon' />
          Player2
        </obg-button-group-item>
      </obg-button-group>
    </obg-footer>
  </div>
</template>

<script>
import header from '../components/header'
import footer from '../components/footer'
import button from '../components/button'
import clock from '../components/clock'
import album from '../components/svg-icon/Album.vue'
import progressBar from '../components/progress'
import temperature from '../components/temperature'
import {ButtonGroup,ButtonGroupItem} from '../components/button-group'
import spinner from '../components/spinner'
import popup from '../components/popup'

export default {
  name: 'test',
  components: {
    'obg-header': header,
    'obg-footer': footer,
    'obg-button': button,
    'obg-button-group':ButtonGroup,
    'obg-button-group-item':ButtonGroupItem,
    'obg-clock': clock,
    'obg-temperature': temperature,
    'album-icon': album,
    'obg-progress': progressBar,
    'obg-spinner':spinner,
    'obg-popup':popup
  },
  data () {
    return {
      pValue:20,
      buffer:40
    }
  },
  methods: {
    handleClick (evt) {
      console.log(evt)
    },
    progressUp(){
      this.pValue += 10
    },
    progressDown(){
      this.pValue -= 10
    },
    bufferUp(){
      this.buffer += 10
    },
    bufferDown(){
      this.buffer -= 10
    },

    onBack (evt) {
      console.log(evt)
      if(window.applicationManager) {
        window.applicationManager.getOwnerApplication(window.document).back();
      }
    },
    onMore (evt) {
      console.log(evt)
    },
    consoleIndex () {
      console.log('click btnGroup', this.btnGroup)
    }
  }
}
</script>
<style>
  .contents{
      position: relative;
      width: 800px;
      height: 480px;
      background:#2a2a2a;
      color: #ffffff;
  }
  .spinner{
    position:absolute;
    top: 100px;
    left: 600px;
  }
  button{
    margin:5px;
    float:left;
  }
  p{
    clear:both;
  }
  .icon{

  }
  button.custom-css{
    width:100px;
    height:100px;
    background-color:red;
    border:none;
    box-shadow:none;
    border-radius:50%;
  }
  button.custom-css:active{
    background-color:#2040aa;
    border:2px solid #445020;
  }
  .test{
    width:200px;
  }
  .p-b-test{

  }
</style>
