<template>
        <div class="contents">
            <obg-list :hideDummyItem=false>
              <obg-list-item v-for='item in items' @click=navPage :name=item>
                  {{item}}
              </obg-list-item>
            </obg-list>
        </div>
</template>

<script>
import button from '../components/button'
import {list, listItem} from '../components/list'
export default {
  name: 'main',
  components: {
    'obg-button': button,
    'obg-list': list,
    'obg-list-item': listItem
  },
  directives: {
  },
  methods: {
    navPage (evt) {
      var name = evt.target.getAttribute('name')
      this.$router.push('/' + name)
    }
  },
  data () {
    return {
      items: ['icons', 'button', 'button-group', 'tab', 'combobox', 'list', 'gridlist', 'popup', 'dialog', 'test', 'slider', 'range', 'playtime', 'radio', 'popover', 'contextmenu', 'footer', 'listItem', 'cover-flow', 'cover-flow-portrait', 'numeric', 'accordion', 'spinner', 'checkbox', 'progress', 'toggle']
    }
  },
  beforeMount () {
  }
}
</script>
<style lang='scss' scoped>
    .contents{
        background-color:#191919;

    }
</style>
