<template>
  <div class="contents">
    <section>
      <label>button - default</label>
      <div class="column">
        <div class="row">status</div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>normal</div>
        <div>
          <obg-button v-obg-focus="{zone:1, order:1}">Close xxxxxxxxxxxxxxxxxx</obg-button>
        </div>
      </div>
      <div class="column">
        <div>icon(icon="close")</div>
        <div>
          <obg-button icon="close" v-obg-focus="{zone:1, order:2}"></obg-button>
        </div>
      </div>
    </section>
    <section>
      <label>button - default</label>
      <h3>&lt;obg-button icon='close' :disabled=true|false &gt;label&lt;/obg-button&gt;</h3>
      <div class="column">
        <div class="row">status</div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>icon+label</div>
        <div>
          <obg-button icon="close" v-obg-focus="{zone:1, order:3}">Close xxxxxxxxxxxxxx</obg-button>
        </div>
      </div>
      <div class="column">
        <div>disabled(:disabled=true)</div>
        <div>
          <obg-button :disabled=true icon="close" v-obg-focus="{zone:1, order:4}">Close</obg-button>
        </div>
      </div>
    </section>
    <section>
      <label>button - flat</label>
      <div class="column">
        <div class="row">status</div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>normal</div>
        <div>
          <obg-button type="flat" v-obg-focus="{zone:2, order:1}">Close</obg-button>
        </div>
      </div>
      <div class="column">
        <div>icon</div>
        <div>
          <obg-button type="flat" icon="close" v-obg-focus="{zone:2, order:2}"></obg-button>
        </div>
      </div>
      <div class="column">
        <div>icon+label</div>
        <div>
          <obg-button type="flat" icon="close-small" v-obg-focus="{zone:2, order:3}">Close Button</obg-button>
        </div>
      </div>
      <div class="column">
        <div>disabled</div>
        <div>
          <obg-button type="flat" :disabled=true icon="close-small" v-obg-focus="{zone:2, order:4}">Close</obg-button>
        </div>
      </div>
    </section>
    <section>
      <label>button - round</label>
      <div class="column">
        <div class="row">status</div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>normal</div>
        <div>
          <obg-button type="round" v-obg-focus="{zone:3, order:1}">Close</obg-button>
        </div>
      </div>
      <div class="column">
        <div>icon</div>
        <div>
          <obg-button type="round" icon="close" v-obg-focus="{zone:3, order:2}"></obg-button>
        </div>
      </div>
      <div class="column">
        <div>icon+label</div>
        <div>
          <obg-button type="round" icon="close-small" v-obg-focus="{zone:3, order:3}">Close Button</obg-button>
        </div>
      </div>
      <div class="column">
        <div>disabled</div>
        <div>
          <obg-button type="round" :disabled=true icon="close-small" v-obg-focus="{zone:3, order:4}">Close</obg-button>
        </div>
      </div>
    </section>
    <section>
      <label>button - event</label>
      <div class="column">
        <div class="row">event </div>
        <div class="row">exam</div>
      </div>
      <div class="column">
        <div>@click</div>
        <div>
          <obg-button type="round" @click="add">Add</obg-button>
        </div>
      </div>
      <div class="column">
        <div>@click</div>
        <div>
          <obg-button type="round" @click="clear">Clear</obg-button>
        </div>
      </div>
      <div class="column">
        <div></div>
        <div>
          <textarea ref="textarea" :style="{height : 80 + 'px'}"></textarea>
        </div>
      </div>
    </section>
  </div>
</template>

<script>
import button from '../components/button'

export default {
  name: 'template',
  components: {
    'obg-button': button
  },
  data () {
    return {
      model: []
    }
  },
  methods: {
    add () {
      this.$refs.textarea.value += 'click! '
    },
    clear () {
      this.$refs.textarea.value = ''
    }
  }
}
</script>
<style lang='scss' scoped>
  .contents{
    position: relative;
    width: 800px;
    height: 480px;
    background:#2a2a2a;
    color: #ffffff;
    overflow-y: auto;
    & > .guide {
      display: block;
      margin: 10px;
      border-bottom:1px solid white;
      & > span {
        line-height:24px;
      }
    }
    & > section {
      display: block;
      margin: 10px;
      height:160px;
      border-bottom: 1px solid grey;
      & > label {
        display:block;
        margin: 5px;
      }
      & > div.column{
        float:left;
        padding:2px;
        & > div{
          margin: auto;
          & > button {
            margin-top:5px;
          }
          &:first-child{
            height:30px;
            line-height:30px;
            text-align: center;
            border-bottom: 1px solid #a9a9a9;
          }
        }
        & > .row{
          text-align: center;
          line-height: 70px;
          border-right: 1px solid #a9a9a9;
          padding:3px;
        }
      }
    }
  }
</style>
