<template>
  <div class="contents">
    <obg-combo-box
      class="combobox"
      :options="option"
      :itemHeight="50"
      @input="onInput"
      @close="onClose"
      @open="onOpen"
      v-model="value"

    >
    </obg-combo-box>
    <obg-combo-box
      class="combobox-2"
      :options="option"
      :value=2
    >
    </obg-combo-box>
    <obg-combo-box
      class="combobox-3"
      :options="option"
      :disabled=true
    >
    </obg-combo-box>
    <div class="label">
      <h1>Event/Model</h1>
      <label>Selected Item Index - {{selectedItemIndex}}</label>
      <label>model - {{value}}</label>
      <label>status - {{status}}</label>
      <obg-button @click="randomValue" >random</obg-button>
    </div>
    <label class="init-value">init by value</label>
    <label class="disable">disable</label>
  </div>
</template>

<script>
  import button from '../components/button'
  import combobox from '../components/combobox'
  export default {
    name: 'template',
    components: {
      'obg-button': button,
      'obg-combo-box': combobox
    },
    data () {
      return {
        option: [{name: 'opt1', label: 'Option'}, {name: 'opt2', label: 'Advanced Setting'}, {name: 'opt3', label: 'Reset'}, {name: 'opt4', label: 'Help'}, {name: 'opt5', label: 'Smart Tutoriels'}],
        newValue: 'opt1',
        value: -1,
        selectedItemIndex: '',
        status: 'close'
      }
    },
    methods: {
      onInput (val) {
        this.selectedItemIndex = val
      },
      randomValue () {
        let random = Math.floor(Math.random() * 5)
        while (this.value === random) {
          random = Math.floor(Math.random() * 5)
        }
        this.value = random
      },
      onOpen () {
        this.status = 'open'
      },
      onClose () {
        this.status = 'close'
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents {
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
    .combobox{
      position: absolute;
      left:10%;
      top: 35%;
    }
    .combobox-2{
      position: absolute;
      left:50%;
      top:10%;
    }
    .combobox-3{
      position: absolute;
      position: absolute;
      top: 150px;
      left:50%;
    }

    .label{
      position:absolute;
      top: 5%;
      left: 10%;
      & > label {
        display:block;
      }
      & > button {
        margin: auto;
      }
    }
    .init-value{
      position:absolute;
      left:50%;
      top:5%;
    }
    .disable{
      position:absolute;
      left:50%;
      top:25%;
    }
  }

</style>
