<template>
  <div class="contents">
    <div class="label">
      <div>context-menu status : {{status}}</div>
      <div>input value : {{input}}</div>
      <br />
      <div>Options should be [{name: 'opt1', label: 'Option'}, ....] </div>
    </div>
    <obg-context-menu
      class="context-menu"
      icon="more"
      :options="option"
      @open="onOpen"
      @close="onClose"
      @input="onInput"
    >
    </obg-context-menu>
  </div>
</template>

<script>
  import button from '../components/button'
  import contextmenu from '../components/context-menu'
  export default {
    name: 'template',
    components: {
      'obg-button': button,
      'obg-context-menu': contextmenu
    },
    data () {
      return {
        option: [{name: 'opt1', label: 'Option'}, {name: 'opt2', label: 'Advanced Setting'}, {name: 'opt3', label: 'Reset'}, {name: 'opt4', label: 'Help'}, {name: 'opt5', label: 'Smart Tutoriels'}],
        status: 'close',
        input: -1
      }
    },
    methods: {
      onOpen () {
        this.status = 'open'
      },
      onClose () {
        this.status = 'close'
      },
      onInput (val) {
        this.input = val
      }
    }
  }
</script>
<style lang='scss' scoped>
    .contents {
      position: relative;
      width: 800px;
      height: 480px;
      background: #2a2a2a;
      color: #ffffff;
      .context-menu{
        position: absolute;
        left:80%;
        top:80%;
        z-index: 150;
      }
      .label {
        width: 100%;
        text-align: center;
        top: 40%;
        position: relative;
      }
    }

</style>
