<template>
  <div class="contents">
    <obg-button @click='showDialog' >dialog</obg-button>
    <obg-dialog :title='test' :show='dShow' @close='closeDialog' >
      <div>
        <h1>content</h1>
        <p>hello! this is a dialog component</p>
      </div>
    </obg-dialog>

  </div>
</template>

<script>
  import dialog from '../components/dialog'
  import button from '../components/button'

  export default {
    name: 'template',
    components: {
      'obg-button': button,
      'obg-dialog': dialog
    },
    data: function () {
      return {
        dShow: false,
        test: 'im dialog'
      }
    },
    methods: {
      showDialog () {
        this.dShow = true
      },
      closeDialog () {
        this.dShow = false
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents{
    position: relative;
    width: 800px;
    height: 480px;
    background:#2a2a2a;
    color: #ffffff;
  }
</style>
