<template>
  <div class="contents">
    <div class="label">
      <label>Input - {{inputValue}}</label>
      <label>Event - {{eventName}}</label>
    </div>
    <obg-footer
      @input="onInput"
      @back="onBack"
      @open="onOpen"
      @close="onClose"
    ></obg-footer>
  </div>
</template>

<script>
  import footer from '../components/footer'
  export default {
    name: 'template',
    components: {
      'obg-footer': footer
    },
    data () {
      return {
        inputValue: -1,
        eventName: 'none'
      }
    },
    methods: {
      onInput (val) {
        this.inputValue = val
      },
      onBack (e) {
        this.eventName = 'back'
      },
      onOpen (e) {
        this.eventName = 'open'
      },
      onClose (e) {
        this.eventName = 'close'
      }
    }
  }
</script>
<style lang='scss' scoped>
    .contents {
        position: relative;
        width: 800px;
        height: 480px;
        background: #2a2a2a;
        color: #ffffff;
    }
  .label {
    position:absolute;
    top:40%;
    left:40%;
    & > label {
      display:block;
    }
  }
</style>
