<template>
  <div class="contents">
    <obg-grid-list :row='row' :col='col' >
      <obg-grid-list-item v-for='item in items' >{{item}}</obg-grid-list-item>
    </obg-grid-list>
    <div class='btn-area' >
      <obg-button @click='upRow' >row up</obg-button>
      <obg-button @click='downRow' >row down</obg-button>
      <div class='attr-info' >
        row = {{row}}, col = {{col}}
      </div>
      <obg-button @click='upCol' >col up</obg-button>
      <obg-button @click='downCol' >col down</obg-button>
    </div>
  </div>
</template>

<script>
  import {gridList, gridListItem} from '../components/grid-list'
  import button from '../components/button'

  export default {
    name: 'template',
    components: {
      'obg-grid-list': gridList,
      'obg-grid-list-item': gridListItem,
      'obg-button': button
    },
    methods: {
      upRow () {
        this.row ++
      },
      downRow () {
        this.row --
        if (this.row === 0) {
          this.row = 1
        }
      },
      upCol () {
        this.col ++
      },
      downCol () {
        this.col --
        if (this.col === 0) {
          this.col = 1
        }
      }
    },
    data () {
      return {
        row: 2,
        col: 4,
        items: []
      }
    },
    mounted () {
      for (var i = 0; i < 20; i++) {
        this.items.push(i)
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents{
    position: relative;
    width: 800px;
    height: 480px;
    background:#2a2a2a;
    color: #ffffff;
  }
  .btn-area{
    .attr-info{
      text-align:center;
    }
    align-items:center;
    justify-content:space-between;
    display:flex;
    flex-direction:row;
  }
</style>
