<template>
  <div class="contents">
    <obg-list>
      <obg-list-item v-for='l in list' @click='goSubList(l)' >
        <div class='content' >
          <h1>{{l.name}} </h1>
        </div>
        <div class='tail' >
          <div class='obg-icon-chevron-right icon' ></div>
        </div>
      </obg-list-item>
    </obg-list>

  </div>
</template>

<script>
  import {list, listItem} from '../components/list'
  import checkbox from '../components/checkbox'
  import button from '../components/button'

  export default {
    name: 'template',
    components: {
      'obg-list': list,
      'obg-checkbox': checkbox,
      'obg-list-item': listItem,
      'obg-button': button
    },
    methods: {
      goSubList (list) {
        console.log(list)
        this.$router.push('/list/' + list.url)
      }
    },
    data () {
      return {
        list: [
          {
            name: 'No list',
            url: 'noList'
          },
          {
            name: 'List loading 1',
            url: 'listLoading1'
          },
          {
            name: 'List loading 2',
            url: 'listLoading2'
          },
          {
            name: 'Fixed list item',
            url: 'fixedListItem'
          }
        ]
      }
    },
    mounted () {
    }
  }
</script>
<style lang='scss' scoped>
  .icon{
    width:50px;
    height:50px;
  }
</style>
