<template>
  <div class="contents">
    <obg-list>
      <obg-list-item :fixed='true' >
        <div class='fixed-item' >
          <h1>this is fixed list item</h1>
        </div>
      </obg-list-item>

      <obg-list-item v-for='item in items' >
        list item {{item}} 
      </obg-list-item>

    </obg-list>
    <div class='btn-area' >
      <obg-button @click='back' >back</obg-button>
    </div>

  </div>
</template>

<script>
  import {list, listItem} from '../../components/list'
  import button from '../../components/button'

  export default {
    name: 'template',
    components: {
      'obg-list': list,
      'obg-list-item': listItem,
      'obg-button': button
    },
    methods: {
      back () {
        this.$router.back()
      }
    },
    data () {
      return {
        items: []
      }
    },
    mounted () {
      for (var i = 0; i < 30; i++) {
        this.items.push(i)
      }
    }
  }
</script>
<style lang='scss' scoped>
  .fixed-item{
    text-align:center;
  }
  .btn-area{
    align-items:center;
    justify-content:space-between;
    display:flex;
    flex-direction:row;
  }
</style>
