<template>
  <div class="contents">
    <obg-list>
      <obg-list-item :fixed='true' >
        <div class='loading' >
          <obg-spinner :overlay='false'></obg-spinner>
          <h2>
            Downloading in progress
          </h2>
        </div>
        
      </obg-list-item>

      <obg-list-item v-for='item in items' >
        list item {{item}} 
      </obg-list-item>

    </obg-list>
    <div class='btn-area' >
      <obg-button @click='back' >back</obg-button>
    </div>

  </div>
</template>

<script>
  import {list, listItem} from '../../components/list'
  import button from '../../components/button'
  import spinner from '../../components/spinner'

  export default {
    name: 'template',
    components: {
      'obg-list': list,
      'obg-list-item': listItem,
      'obg-spinner': spinner,
      'obg-button': button
    },
    methods: {
      back () {
        this.$router.back()
      }
    },
    data () {
      return {
        items: []
      }
    },
    mounted () {
      for (var i = 0; i < 4; i++) {
        this.items.push(i)
      }
    }
  }
</script>
<style lang='scss' scoped>
  .loading{
    padding-left:10px;
    padding-right:10px;
    display:flex;
    flex-direction:row;
    align-items:center;
    justify-content:flex-start;
    h2{
      padding-left:20px;
    }
  }
  .btn-area{
    align-items:center;
    justify-content:space-between;
    display:flex;
    flex-direction:row;
  }
</style>
