<template>
  <div class="contents">
    <obg-list>
      <div class='empty-inform' v-if='items.length == 0' >
        <h1 class='main-txt'>
          No List
        </h1>
        <p>
          blah blah blah blah ....
        </p>
      </div>
      <obg-list-item v-for='item in items' > {{item}} </obg-list-item>
    </obg-list>
    <div class='btn-area' >
      <obg-button @click='back' >back</obg-button>
      <obg-button @click='add' >add</obg-button>
      <obg-button @click='del' >delete</obg-button>
    </div>
  </div>
</template>

<script>
  import {list, listItem} from '../../components/list'
  import checkbox from '../../components/checkbox'
  import button from '../../components/button'

  export default {
    name: 'template',
    components: {
      'obg-list': list,
      'obg-checkbox': checkbox,
      'obg-list-item': listItem,
      'obg-button': button
    },
    methods: {
      back () {
        this.$router.back()
      },
      add () {
        this.items.push(this.count++)
      },
      del () {
        if (this.items.length !== 0) {
          this.items.splice(0, 1)
        }
      }
    },
    data () {
      return {
        items: [],
        count: 0
      }
    },
    mounted () {
    }
  }
</script>
<style lang='scss' scoped>
  .icon{
    width:50px;
    height:50px;
  }
  .empty-inform{
    height:100%;
    padding:30px 30px;
    text-align:center;
    .main-txt{
      font-size:32px;
      padding:20px 20px;
    }
  }
  .btn-area{
    align-items:center;
    justify-content:space-between;
    display:flex;
    flex-direction:row;
  }
</style>
