<template>
  <div class="contents">
    <div class="calendar">
      <obg-numeric class="numeric dd" :min="1" :max="31" delimiter="/" @input="onInputDay" v-model="dayModel"></obg-numeric>
      <obg-numeric class="numeric mm" :min="1" :max="12" delimiter="/" @input="onInputMonth" v-model="monthModel"></obg-numeric>
      <obg-numeric class="numeric yyyy" :width="400" :min="1900" :max="2999" :showDelimiter="false" @input="onInputYear" v-model="yearModel"></obg-numeric>
    </div>
    <div class="label">
      <label>@input = {{day}}/{{month}}/{{year}}</label>
      <label>model = {{dayModel}}/{{monthModel}}/{{yearModel}}</label>
      <div class="buttons">
        <obg-button @click="monthUp"> mm up</obg-button>
        <obg-button @click="monthDown"> mm down</obg-button>
      </div>
    </div>
</div>
</template>

<script>
  import numeric from '../components/numeric'
  import button from '../components/button'

  export default {
    name: 'numeric',
    components: {
      'obg-numeric': numeric,
      'obg-button': button
    },
    data () {
      return {
        day: 17,
        month: 2,
        year: 2017,
        dayModel: 17,
        monthModel: 2,
        yearModel: 2017
      }
    },
    methods: {
      onInputDay (val) {
        this.day = val
      },
      onInputMonth (val) {
        this.month = val
      },
      onInputYear (val) {
        this.year = val
      },
      monthUp () {
        this.monthModel += 1
        if (this.monthModel > 12) this.monthModel = 12
      },
      monthDown () {
        this.monthModel -= 1
        if (this.monthModel < 1) this.monthModel = 1
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents{
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
    & > .calendar{
      margin-top:20px;
      margin-left:20px;
      display: flex;
      font-size: 24px;
    }
    .label{
      position: absolute;
      left: 30%;
      top: 65%;
      font-size: 20px;
      & > label{
        display:block;
      }
    }
  }
</style>
