<template>
  <div class="contents">
    <div class="label">
      <label>value = {{value}}</label>
      <label>buffer = {{buffer}}</label>
      <br/>
      <div class="buttons">
        Value
        <obg-button type="round" @click="valueUp">Up</obg-button>
        <obg-button type="round" @click="valueDown">Down</obg-button>
      </div>
      <div class="buttons">
        Buffer
        <obg-button type="round" @click="bufferUp">Up</obg-button>
        <obg-button type="round" @click="bufferDown">Down</obg-button>
      </div>
    </div>
    <obg-progress :value="value" :buffer="buffer" class="progress">
          <span slot='start'>start</span>
          <span slot='end'>end</span>
    </obg-progress>
  </div>
</template>

<script>
  import button from '../components/button'
  import progress from '../components/progress'

  export default {
    name: 'template',
    components: {
      'obg-button': button,
      'obg-progress': progress
    },
    data () {
      return {
        value: 0,
        buffer: 10,
        unit: 10
      }
    },
    methods: {
      valueUp () {
        this.value += this.unit
      },
      valueDown () {
        this.value -= this.unit
      },
      bufferUp () {
        this.buffer += this.unit
      },
      bufferDown () {
        this.buffer -= this.unit
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents {
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
  }
  .progress {
    position: absolute;
    top: 50%;
    left: 5%;
  }
  .label{
    position: absolute;
    top:10%;
    left: 40%;
    & > .buttons{
      text-align:center;
      margin:5px;
      display:inline-block;
    }
  }
</style>
