<template>
  <div class="contents">
    <obg-list :hideDummyItem=false>
      <obg-list-item >
        <div class='content' >Radio 1 / selected val = {{option}} @input = {{inputVal1}}</div>
      </obg-list-item>
      <obg-list-item >
        <div class='content' >
          val = opt1
        </div>
        <div class='tail' >
          <obg-radio
            v-model="option"
            val="opt1"
            @input="onInput"
          >
          </obg-radio>
        </div>
      </obg-list-item>
      <obg-list-item >
        <div class='content' >
          val = opt2
        </div>
        <div class='tail' >
          <obg-radio
            v-model="option"
            @input="onInput2"
            val="opt2"
          >
          </obg-radio>
        </div>
      </obg-list-item>
      <obg-list-item >
        <div class='content' >
          val = opt3
        </div>
        <div class='tail' >
          <obg-radio
            v-model="option"
            @input="onInput"
            val="opt3"
          >
          </obg-radio>
        </div>
      </obg-list-item>

    </obg-list>
    <div style="margin:10px">
      <div>Radio 2 / selected val = {{option2}} / @input = {{inputVal2}}</div>
      <obg-radio
        v-model="option2"
        val="a"
        @input="onInput2"
      >
      </obg-radio>
      <obg-radio
        v-model="option2"
        val="b"
        @input="onInput2"
      >
      </obg-radio>
      <obg-radio
        v-model="option2"
        val="c"
        @input="onInput2"
        :disabled=true
      >
      </obg-radio>
    </div>

  </div>
</template>

<script>
  import radio from '../components/radio'
  import {list, listItem} from '../components/list'
  export default {
    name: 'radio',
    components: {
      'obg-radio': radio,
      'obg-list': list,
      'obg-list-item': listItem
    },
    methods: {
      onInput (val) {
        this.inputVal1 = val
      },
      onInput2 (val) {
        this.inputVal2 = val
      }
    },
    data () {
      return {
        option: 'opt1',
        option2: 'a',
        inputVal1: '',
        inputVal2: ''
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents {
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
  }
  .obg-list{
    height:300px !important;
  }

</style>
