<template>
  <div class="contents">
    <obg-list :hideDummyItem=false>
      <obg-list-item>
        <div class='content' >
          range in list-item / value = {{model1}}
        </div>
      </obg-list-item>
      <obg-list-item >
        <obg-range
          class="range"
          @input="onInput"
          v-model="model1"
        >
        </obg-range>
      </obg-list-item>
      <obg-list-item>
        <div class='content' >
          step = 5% / max = 500 / value = {{model2}}
        </div>
      </obg-list-item>
      <obg-list-item >
        <obg-range
          class="range"
          :min="0"
          :max="500"
          :stepPercent="5"
          v-model="model2"
          @input="onInput"
        >
        </obg-range>
      </obg-list-item>
      <obg-list-item>
        <div class='content' >
          disabled
        </div>
      </obg-list-item>
      <obg-list-item >
        <obg-range
          class="range"
          :disabled=true
        >
        </obg-range>
      </obg-list-item>
    </obg-list>
  </div>
</template>

<script>
  import range from '../components/range'
  import {list, listItem} from '../components/list'
  export default {
    name: 'range',
    components: {
      'obg-range': range,
      'obg-list': list,
      'obg-list-item': listItem
    },
    methods: {
      onInput (val) {}
    },
    data () {
      return {
        model1: 0,
        model2: 100,
        model3: 50
      }
    }
  }
</script>
<style lang='scss' scoped>
  .contents {
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
  }
  .range{
    margin:5px;
  }
</style>
