<template>
    <div class="contents">
      <h1>Slider</h1>
      <section>
        <label>min=10 max=50</label>
        <label>value = {{model1}}</label>
        <obg-slider
          class="slider"
          v-model="model1"
          :min="10"
          :max="50"
          @input="onInput"
        />
      </section>

      <section>
        <label>min=0 max=500</label>
        <label>value = {{model2}}</label>
        <obg-slider
          class="slider"
          v-model="model2"
          :min="0"
          :max="500"
          @input="onInput"
        />
      </section>

      <section>
        <label>disabled</label>
        <obg-slider
          class="slider"
          :disabled=true
        >
        </obg-slider>
      </section>

      <section >
        <label>min=0 max=100 (default)</label>
        <label>init by value = 50</label>
        <label>value = {{model3}}</label>
        <obg-slider
          class="slider"
          v-model="model3"
          @input="onInput"
        />
        <div class="buttons">
          <obg-button type="round" @click="up">up</obg-button>
          <obg-button type="round" @click="down">down</obg-button>
        </div>
      </section>

    </div>
</template>

<script>
import slider from '../components/slider'
import button from '../components/button'

export default {
  name: 'slider',
  components: {
    'obg-slider': slider,
    'obg-button': button
  },
  methods: {
    onInput (val) {

    },
    up () {
      this.model3 += 10
      if (this.model3 > 100) {
        this.model3 = 100
      }
    },
    down () {
      this.model3 -= 10
      if (this.model3 < 0) {
        this.model3 = 0
      }
    }
  },
  data () {
    return {
      model1: 0,
      model2: 100,
      model3: 50
    }
  }
}
</script>
<style lang='scss' scoped>
  @import './css/section.scss';
  .contents {
    position: relative;
    width: 800px;
    height: 480px;
    background: #2a2a2a;
    color: #ffffff;
    & > section {
      height: 100px;
      & > div.buttons{
        & > button {
          display:inline-block;
        }
      }
      &:last-child{
        height:200px;
      }
    }
  }
  .slider{
    margin:5px;
  }


</style>
