<template>
    <div class="contents">
      <div class="label">
        <label>Spinner</label>
        <obg-button @click="onClick" >{{flag ? 'close' : 'open'}}</obg-button>
        <label>Label on/off</label>
        <obg-button @click="onClick2" >{{label ? 'off' : 'on'}}</obg-button>
      </div>
        <obg-spinner ref="spin" v-show="flag" @click="onClickSpinner">{{label}}</obg-spinner>
    </div>
</template>

<script>
  import spinner from '../components/spinner'
  import button from '../components/button'

  export default {
    components: {
      'obg-spinner': spinner,
      'obg-button': button
    },
    mounted () {
    },
    data () {
      return {
        flag: false,
        label: ''
      }
    },
    methods: {
      onClick () {
        this.flag = !this.flag
      },
      onClick2 () {
        if (this.label) {
          this.label = ''
        } else {
          this.label = 'Loading'
        }
      },
      onClickSpinner () {
        console.log('click!')
      }
    }
  }
</script>
<style lang='scss' scoped>
    .contents {
        position: relative;
        width: 800px;
        height: 480px;
        background: #2a2a2a;
        color: #ffffff;
    }
    .label{
      position:absolute;
      left:5%;
    }
</style>
