<template>
<div class="contents">
  <div class='row' >
    <h1>1. basic usage</h1>
    <div class='ex' >
      <obg-toggle v-model='vm' ></obg-toggle>
      <p>{ vm : {{vm}} }</p>
    </div>
  </div>
  <div class='row' >
    <h1>2. disable</h1>
    <div class='ex' >
      <obg-toggle v-model='vm2' :disabled=true >disabled &amp; checked</obg-toggle>
      <br/>
      <obg-toggle v-model='vm3' :disabled=true >disabled &amp; not checked</obg-toggle>
    </div>
  </div>
  <div class='row' >
    <h1>3. @input</h1>
    <div class='ex' >
      <p>register @input event handler</p>
      <obg-toggle v-model='vm4' @input='change' ></obg-toggle>
      <p>
      {{str}} 
      </p>
    </div>
  </div>
  

</div>
</template>

<script>
  import toggle from '../components/toggle'

  export default {
    name: 'template',
    components: {
      'obg-toggle': toggle
    },
    methods: {
      change (val) {
        if (val) {
          this.str = 'vm4 is checked'
        } else {
          this.str = 'vm4 is not checked'
        }
      }
    },
    data () {
      return {
        vm: true,
        vm2: true,
        vm3: false,
        vm4: true,
        str: 'vm4 is not checked'
      }
    },
    mounted () {
    }
  }
</script>
<style lang='scss' scoped>
  .contents{
    position: relative;
    width: 800px;
    height: 480px;
    background:#2a2a2a;
    color: #ffffff;
    .row{
      padding:10px 10px;
      h1{
        font-size:28px;
      }
    }
    .ex{
      padding:10px 10px;
      p{
        font-size:15px;
      }
    }
  }
</style>
